WITH TEM1 AS (SELECT PLAYER_ID, EVENT_DATE, E1, E2 FROM (SELECT PLAYER_ID, EVENT_DATE, LEAD(EVENT_DATE) OVER(ORDER BY PLAYER_ID,event_date) E1 ,(EVENT_DATE+1) E2 FROM ACTIVITY) WHERE (PLAYER_ID,event_date) IN (SELECT PLAYER_ID, MIN(EVENT_DATE) FROM ACTIVITY GROUP BY PLAYER_ID))
SELECT ROUND(((SELECT COUNT(PLAYER_ID) FROM TEM1 WHERE E1=E2)/(SELECT COUNT((PLAYER_ID)) FROM TEM1)),2) fraction FROM DUAL;